import React from 'react';
import { useForm, useToggle, upperFirst } from '@mantine/hooks';
import {
  TextInput,
  PasswordInput,
  Text,
  Paper,
  Group,
  PaperProps,
  Button,
  Divider,
  Checkbox,
  useMantineTheme,
} from '@mantine/core';
import { GoogleButton, TwitterButton } from '../SocialButtons/SocialButtons';

export function AuthenticationForm(props: PaperProps<'div', HTMLDivElement>) {
  const theme = useMantineTheme();
  const [type, toggle] = useToggle('login', ['login', 'register']);
  const form = useForm({
    initialValues: {
      email: '',
      name: '',
      password: '',
      terms: true,
    },

    validationRules: {
      email: (val) => /^\S+@\S+$/.test(val),
      password: (val) => val.length >= 6,
    },
  });

  return (
    <Paper
      shadow="sm"
      radius="md"
      padding="xl"
      {...props}
      style={{
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.white,
        ...props.style,
      }}
    >
      <Text size="lg" weight={500}>
        Welcome to Mantine, {type} with
      </Text>

      <Group grow style={{ marginTop: 5, marginBottom: 15 }}>
        <GoogleButton radius="xl">Google</GoogleButton>
        <TwitterButton radius="xl">Twitter</TwitterButton>
      </Group>

      <Divider label="Or continue with email" labelPosition="center" />

      <form onSubmit={form.onSubmit(() => {})}>
        <Group direction="column" grow style={{ marginTop: 5 }}>
          {type === 'register' && (
            <TextInput
              label="Name"
              placeholder="Your name"
              value={form.values.name}
              onChange={(event) => form.setFieldValue('name', event.currentTarget.value)}
            />
          )}

          <TextInput
            required
            label="Email"
            placeholder="hello@mantine.dev"
            value={form.values.email}
            onChange={(event) => form.setFieldValue('email', event.currentTarget.value)}
            error={form.errors.email && 'Invalid email'}
          />

          <PasswordInput
            required
            label="Password"
            placeholder="Your password"
            value={form.values.password}
            onChange={(event) => form.setFieldValue('password', event.currentTarget.value)}
            error={form.errors.password && 'Password should include at least 6 characters'}
          />

          {type === 'register' && (
            <Checkbox
              label="I accept terms and conditions"
              checked={form.values.terms}
              onChange={(event) => form.setFieldValue('terms', event.currentTarget.checked)}
            />
          )}
        </Group>

        <Group position="apart" style={{ marginTop: 15 }}>
          <Button variant="link" color="gray" onClick={() => toggle()} size="xs">
            {type === 'register'
              ? 'Already have an account? Login'
              : "Don't have an account? Register"}
          </Button>
          <Button type="submit">{upperFirst(type)}</Button>
        </Group>
      </form>
    </Paper>
  );
}

import React from 'react';
import cx from 'clsx';
import { UnstyledButton, Checkbox, Text, createStyles } from '@mantine/core';
import { useUncontrolled } from '@mantine/hooks';

const useStyles = createStyles((theme) => ({
  button: {
    display: 'flex',
    width: '100%',
    border: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.colors.gray[3]
    }`,
    borderRadius: theme.radius.sm,
    padding: theme.spacing.lg,
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.white,

    '&:hover': {
      backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[9] : theme.colors.gray[0],
    },
  },

  checkbox: {
    marginRight: theme.spacing.xl,
  },

  title: {
    marginBottom: 7,
    lineHeight: 1,
  },
}));

interface CheckboxCardProps {
  checked?: boolean;
  defaultChecked?: boolean;
  onChange?(checked: boolean): void;
  title: React.ReactNode;
  description: React.ReactNode;
}

export function CheckboxCard({
  checked,
  defaultChecked,
  onChange,
  title,
  description,
  className,
  ...others
}: CheckboxCardProps & Omit<React.ComponentPropsWithoutRef<'button'>, keyof CheckboxCardProps>) {
  const classes = useStyles();

  const [value, handleChange] = useUncontrolled({
    value: checked,
    defaultValue: defaultChecked,
    finalValue: false,
    onChange,
    rule: (val) => typeof val === 'boolean',
  });

  return (
    <UnstyledButton
      {...others}
      onClick={() => handleChange(!value)}
      className={cx(classes.button, 'hello', className)}
    >
      <Checkbox
        className={classes.checkbox}
        checked={value}
        onChange={() => {}}
        tabIndex={-1}
        size="md"
      />
      <div>
        <Text weight={500} className={classes.title}>
          {title}
        </Text>
        <Text size="sm" color="dimmed">
          {description}
        </Text>
      </div>
    </UnstyledButton>
  );
}

import React from 'react';
import Flag from 'react-flagpack';
import {
  useMantineTheme,
  CloseButton,
  MultiSelect,
  MultiSelectValueProps,
  SelectItemProps,
} from '@mantine/core';
import { countriesData } from './countries-data';

function Value({
  value,
  label,
  onRemove,
  themeOverride,
  classNames,
  ...others
}: MultiSelectValueProps & { value: string }) {
  const theme = useMantineTheme(themeOverride);

  return (
    <div {...others}>
      <div
        style={{
          display: 'flex',
          cursor: 'default',
          alignItems: 'center',
          backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[7] : theme.white,
          border: `1px solid ${
            theme.colorScheme === 'dark' ? theme.colors.dark[7] : theme.colors.gray[4]
          }`,
          paddingLeft: 10,
          borderRadius: 4,
        }}
      >
        <div style={{ marginRight: 10 }}>
          <Flag code={value} size="S" />
        </div>
        <div style={{ lineHeight: 1, fontSize: 12 }}>{label}</div>
        <CloseButton onClick={onRemove} variant="transparent" size={22} iconSize={14} />
      </div>
    </div>
  );
}

function Item({ label, value, elementRef, ...others }: SelectItemProps) {
  return (
    <div ref={elementRef} {...others}>
      <div style={{ display: 'flex' }}>
        <div style={{ marginRight: 10 }}>
          <Flag code={value} size="S" />
        </div>
        <div>{label}</div>
      </div>
    </div>
  );
}

export function CountriesSelect() {
  return (
    <MultiSelect
      data={countriesData}
      limit={20}
      valueComponent={Value}
      itemComponent={Item}
      searchable
      defaultValue={['US', 'DE']}
      placeholder="Pick countries"
      label="Which countries did you visit last year?"
    />
  );
}

import React from 'react';
import cx from 'clsx';
import { Sun, Phone, MapPin, AtSign } from 'react-feather';
import {
  Paper,
  Text,
  TextInput,
  Textarea,
  Button,
  Group,
  useMantineTheme,
  createStyles,
} from '@mantine/core';
import bg from './bg.svg';

const BREAKPOINT = '@media (max-width: 755px)';

const useStyles = createStyles((theme) => ({
  wrapper: {
    display: 'flex',
    backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.white,
    borderRadius: theme.radius.lg,
    padding: 4,
    border: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.colors.gray[2]
    }`,

    [BREAKPOINT]: {
      flexDirection: 'column',
    },
  },

  form: {
    boxSizing: 'border-box',
    flex: 1,
    padding: theme.spacing.xl,
    paddingLeft: theme.spacing.xl * 2,
    borderLeft: 0,

    [BREAKPOINT]: {
      padding: theme.spacing.md,
      paddingLeft: theme.spacing.md,
    },
  },

  fields: {
    marginTop: -12,
  },

  fieldInput: {
    flex: 1,

    '& + &': {
      marginLeft: theme.spacing.md,

      [BREAKPOINT]: {
        marginLeft: 0,
        marginTop: theme.spacing.md,
      },
    },
  },

  fieldsGroup: {
    display: 'flex',

    [BREAKPOINT]: {
      flexDirection: 'column',
    },
  },

  field: {
    '& + &': {
      marginTop: theme.spacing.sm,
    },
  },

  contacts: {
    boxSizing: 'border-box',
    position: 'relative',
    borderRadius: theme.radius.lg - 2,
    backgroundImage: `url(${bg})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    border: '1px solid transparent',
    padding: theme.spacing.xl,
    flex: '0 0 280px',

    '& *': {
      color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.white,
    },

    [BREAKPOINT]: {
      marginBottom: theme.spacing.sm,
      paddingLeft: theme.spacing.md,
    },
  },

  title: {
    marginBottom: theme.spacing.xl * 1.5,

    [BREAKPOINT]: {
      marginBottom: theme.spacing.xl,
    },
  },

  control: {
    [BREAKPOINT]: {
      flex: 1,
    },
  },
}));

function Contact({
  className,
  title,
  description,
  icon: Icon,
}: {
  className?: string;
  title: string;
  description: string;
  icon: React.FC<React.ComponentProps<typeof Sun>>;
}) {
  const theme = useMantineTheme();
  return (
    <div style={{ display: 'flex', alignItems: 'flex-start' }} className={className}>
      <Icon size={22} style={{ marginRight: theme.spacing.md }} />
      <div>
        <Text
          size="xs"
          style={{
            color: theme.colors.blue[0],
          }}
        >
          {title}
        </Text>
        <Text weight={500}>{description}</Text>
      </div>
    </div>
  );
}

export function GetInTouch() {
  const classes = useStyles();

  return (
    <Paper shadow="md" radius="lg">
      <div className={classes.wrapper}>
        <div className={classes.contacts}>
          <Text size="lg" weight={700} className={classes.title}>
            Contact information
          </Text>

          <Group direction="column" spacing="xl">
            <Contact title="Email" description="hello@mantine.dev" icon={AtSign} />
            <Contact title="Phone" description="+49 (800) 335 35 35" icon={Phone} />
            <Contact title="Address" description="844 Morris Park avenue" icon={MapPin} />
            <Contact title="Working hours" description="8 a.m. â€“ 11 p.m." icon={Sun} />
          </Group>
        </div>

        <form className={classes.form} onSubmit={(event) => event.preventDefault()}>
          <Text size="lg" weight={700} className={classes.title}>
            Get in touch
          </Text>

          <div className={classes.fields}>
            <div className={cx(classes.field, classes.fieldsGroup)}>
              <TextInput className={classes.fieldInput} label="Your name" placeholder="Your name" />
              <TextInput
                className={classes.fieldInput}
                label="Your email"
                placeholder="hello@mantine.dev"
                required
              />
            </div>

            <TextInput className={classes.field} label="Subject" placeholder="Subject" required />

            <Textarea
              className={classes.field}
              label="Your message"
              placeholder="Please include all relevant information"
              minRows={3}
            />

            <Group position="right" className={classes.field}>
              <Button type="submit" className={classes.control}>
                Send message
              </Button>
            </Group>
          </div>
        </form>
      </div>
    </Paper>
  );
}

import React from 'react';
import cx from 'clsx';
import { createStyles, Autocomplete, NumberInput, Button } from '@mantine/core';
import dayjs from 'dayjs';
import { DateRangePicker } from '@mantine/dates';

const useStyles = createStyles((theme) => ({
  wrapper: {
    '@media (max-width: 755px)': {
      padding: theme.spacing.lg,
    },
  },

  fields: {
    display: 'flex',
    alignItems: 'flex-end',

    '@media (max-width: 755px)': {
      display: 'block',
    },
  },

  field: {
    flex: 1,

    '&:first-of-type $input': {
      borderTopLeftRadius: theme.radius.md,
      borderBottomLeftRadius: theme.radius.md,

      '&:not(:focus)': {
        borderLeftColor: theme.colors.gray[2],
      },
    },

    '& + &': {
      '& $input': {
        borderLeftWidth: 0,
        borderRightWidth: 0,

        '@media (max-width: 755px)': {
          borderLeftWidth: 1,
          borderRightWidth: 1,
        },
      },
    },

    '@media (max-width: 755px)': {
      width: '100%',
      maxWidth: '100% !important',

      '&:not(:last-of-type)': {
        marginBottom: theme.spacing.md,
      },
    },
  },

  input: {
    borderColor: [
      [theme.colorScheme === 'dark' ? theme.colors.dark[5] : theme.colors.gray[2]],
      '!important',
    ],
    height: 60,
    paddingLeft: theme.spacing.xl,
    fontSize: theme.fontSizes.md,
    borderLeftColor: theme.colors.gray[4],

    '@media (max-width: 755px)': {
      borderRadius: theme.radius.md,
    },
  },

  inputLabel: {
    marginBottom: 9,
  },

  budgetField: {
    maxWidth: 120,
  },

  destinationField: {
    maxWidth: 280,
  },

  control: {
    height: 60,
    borderTopLeftRadius: 0,
    borderBottomLeftRadius: 0,

    '@media (max-width: 755px)': {
      width: '100%',
      marginLeft: 0,
      marginTop: theme.spacing.lg,
      borderTopLeftRadius: theme.radius.md,
      borderBottomLeftRadius: theme.radius.md,
    },
  },
}));

interface InlineFormProps extends React.ComponentPropsWithoutRef<'div'> {
  labelClassName?: string;
}

export function InlineForm({ labelClassName, ...props }: InlineFormProps) {
  const classes = useStyles();

  return (
    <div {...props} className={cx(classes.wrapper, props.className)}>
      <div className={classes.fields}>
        <Autocomplete
          radius={0}
          size="md"
          data={['Australia', 'United Kingdom', 'Germany', 'Russia', 'Canada']}
          className={cx(classes.field, classes.destinationField)}
          placeholder="Destination country"
          label="Destination"
          variant="default"
          classNames={{ input: classes.input, label: cx(classes.inputLabel, labelClassName) }}
        />

        <DateRangePicker
          radius={0}
          size="md"
          className={classes.field}
          placeholder="Stay dates"
          label="Stay dates"
          minDate={new Date()}
          initialMonth={dayjs(new Date()).add(1, 'month').toDate()}
          inputFormat="MMM D"
          variant="default"
          classNames={{ input: classes.input, label: cx(classes.inputLabel, labelClassName) }}
        />

        <NumberInput
          radius={0}
          size="md"
          className={cx(classes.field, classes.budgetField)}
          label="Budget"
          min={150}
          step={50}
          defaultValue={500}
          hideControls
          variant="default"
          classNames={{ input: classes.input, label: cx(classes.inputLabel, labelClassName) }}
        />

        <Button className={classes.control} radius="md" size="lg">
          Search
        </Button>
      </div>
    </div>
  );
}

import React from 'react';
import { GitHubLogoIcon } from '@modulz/radix-icons';
import { Button, ButtonProps, Group, useMantineTheme } from '@mantine/core';
import { GoogleIcon } from './GoogleIcon';
import { TwitterIcon } from './TwitterIcon';

export function GoogleButton({ style, ...props }: ButtonProps) {
  const theme = useMantineTheme();
  return (
    <Button
      leftIcon={<GoogleIcon />}
      variant="outline"
      color="gray"
      style={{
        ...style,
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.white,
        color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.black,
        borderColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.colors.gray[3],
      }}
      {...props}
    />
  );
}

export function FacebookButton({ style, ...props }: ButtonProps) {
  return <Button style={{ ...style, backgroundColor: '#4267B2', color: '#fff' }} {...props} />;
}

// Generic arguments are used to support custom component in TypeScript
export function TwitterButton<
  C extends React.ElementType = 'button',
  R extends HTMLElement = HTMLButtonElement
>({ style, ...props }: ButtonProps<C, R>) {
  const theme = useMantineTheme();
  return (
    <Button
      leftIcon={<TwitterIcon />}
      variant="outline"
      style={{
        ...style,
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.white,
        borderColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.colors.gray[3],
        color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.black,
      }}
      {...props}
    />
  );
}

export function GithubButton({ style, ...props }: ButtonProps) {
  return (
    <Button
      {...props}
      leftIcon={<GitHubLogoIcon />}
      style={{
        ...style,
        backgroundColor: '#000',
        color: '#fff',
      }}
    />
  );
}

export function SocialButtons() {
  return (
    <Group position="center" withGutter>
      <GoogleButton>Continue with Google</GoogleButton>
      <TwitterButton<'a', HTMLAnchorElement>
        component="a"
        href="https://twitter.com/mantinedev"
        target="_blank"
      >
        Follow on Twitter
      </TwitterButton>
      <FacebookButton>Sign in with Facebook</FacebookButton>
      <GithubButton>View on GitHub</GithubButton>
    </Group>
  );
}
